    { id: 26, name: "I&C of IP 66 Outdoor Rack", unit: "No.", total: 1, work: "Passive - B" }
  ];

  // ---------------------- STATE ----------------------
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [currentCluster, setCurrentCluster] = useState("");
  const [currentStation, setCurrentStation] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [items, setItems] = useState(initialItems);
  const [selectedFolder, setSelectedFolder] = useState("All");
  const [showSaveMessage, setShowSaveMessage] = useState(false);
  const [filteredClusters, setFilteredClusters] = useState([]);
  const [stationsList, setStationsList] = useState([]);

  // ---------------------- LOGIN FUNCTION ----------------------
  const handleLogin = () => {
    if (username === "admin" && password === "admin123") {
      setIsLoggedIn(true);
      setUserRole("admin");
    } else if (username === "worker" && password === "worker123") {
      setIsLoggedIn(true);
      setUserRole("worker");
    } else {
      alert("Invalid credentials!");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole("");
    setUsername("");
    setPassword("");
    setCurrentCluster("");
    setCurrentStation("");
  };

  // ---------------------- SEARCH FILTER ----------------------
  useEffect(() => {
    if (searchQuery.trim()) {
      setFilteredClusters(
        clusters.filter(cl => cl.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    } else {
      setFilteredClusters([]);
    }
  }, [searchQuery]);

  // ---------------------- SELECT CLUSTER ----------------------
  const selectCluster = (clusterName) => {
    setCurrentCluster(clusterName);
    setSearchQuery("");
    setFilteredClusters([]);
    setCurrentStation("");
    const filtered = stationsData.filter(
      (st) => st.cluster.toUpperCase() === clusterName.toUpperCase()
    );
    setStationsList(filtered);
  };

  // ---------------------- SELECT STATION ----------------------
  const selectStation = async (stationName) => {
    setCurrentStation(stationName);
    await loadStationData(currentCluster, stationName);
  };

  // ---------------------- LOAD DATA ----------------------
  const loadStationData = async (cluster, station) => {
    try {
      const key = `${cluster}_${station}`.replace(/\s+/g, '_');
      const result = await window.storage.get(key);
      if (result && result.value) {
        const data = JSON.parse(result.value);
        setItems(data.items || initialItems);
        setSelectedFolder(data.selectedFolder || "All");
      } else {
        setItems(initialItems.map(item => ({ ...item, used: 0 })));
        setSelectedFolder("All");
      }
    } catch (error) {
      setItems(initialItems.map(item => ({ ...item, used: 0 })));
      setSelectedFolder("All");
    }
  };

  // ---------------------- SAVE DATA ----------------------
  const saveStationData = async () => {
    if (!currentCluster || !currentStation) {
      alert("Please select cluster and station first!");
      return;
    }

    try {
      const key = `${currentCluster}_${currentStation}`.replace(/\s+/g, '_');
      const data = {
        cluster: currentCluster,
        station: currentStation,
        items: items,
        selectedFolder: selectedFolder,
        updatedBy: userRole,
        lastUpdated: new Date().toISOString()
      };
      
      await window.storage.set(key, JSON.stringify(data));
      setShowSaveMessage(true);
      setTimeout(() => setShowSaveMessage(false), 3000);
    } catch (error) {
      alert("Error saving data!");
    }
  };

  // ---------------------- UPDATE USED ----------------------
  const updateUsed = (id, increment) => {
    setItems(items.map(item => {
      if (item.id === id) {
        const newUsed = increment ? item.used + 1 : Math.max(0, item.used - 1);
        return { ...item, used: Math.min(newUsed, item.total) };
      }
      return item;
    }));
  };

  const getRemainingColor = (remaining, total) => {
    if (total === 0) return 'bg-gray-100 text-gray-400';
    if (remaining === 0) return 'bg-red-50 text-red-700 border-red-300';
    if (remaining <= total * 0.2) return 'bg-orange-50 text-orange-700 border-orange-300';
    return 'bg-green-50 text-green-700 border-green-300';
  };

  const folders = ["All", "Active", "Passive - A", "Passive - B", "Trenching"];
  const filteredItems = selectedFolder === "All" ? items : items.filter(item => item.work === selectedFolder);

  // ---------------------- LOGIN SCREEN ----------------------
